<?php

namespace App\Response\AffaireNature;

use Illuminate\Http\Resources\Json\ResourceCollection;

class AllAffaireNatureResponse extends ResourceCollection
{
public function toArray($request)
{
 //   return parent::toArray($request); // TODO: Change the autogenerated stub

    $collection=$this->collection->toArray();
    return[
      'affaieNatures'=>array_map(fn($collection)=>new AffaireNatureResponse($collection),$collection)
    ];

}
}
