<?php

namespace App\Response\AffaireSituation;

use Illuminate\Http\Resources\Json\JsonResource;

class AffaireSituationResponse extends JsonResource
{
    public function toArray($request)
    {
        //return parent::toArray($request); // TODO: Change the autogenerated stub

        return[
            "organisation_id"=>property_exists($this,"organisation_id")?$this->organisation_id:$this->resource["organisation_id"],
            "Name"=>property_exists($this,"Name")?$this->Name:$this->resource["Name"],
            "orderChr"=>property_exists($this,"orderChr")?$this->orderChr:$this->resource["orderChr"],
        ];
    }
}
