<?php

namespace App\Response\AffaireSituation;

use Illuminate\Http\Resources\Json\JsonResource;

class AffaireSituationsResponse extends JsonResource
{
    public function toArray($request)
    {
     //   return parent::toArray($request); // TODO: Change the autogenerated stub
        $collection=is_array($this->collection)?$this->collection:$this->collection->toArray();
        return array_map(fn($collection)=>new AffaireSituationResponse($collection),$collection);
    }
}
