<?php

namespace App\Response\BusinessManagements;

use Illuminate\Http\Resources\Json\ResourceCollection;

class BusinessManagementsResponse extends ResourceCollection
{
    public function toArray($request)
    {
     //   return parent::toArray($request); // TODO: Change the autogenerated stub
        $collection=is_array($this->collection)?$this->collection:$this->collection->toArray();
        return array_map(fn($collection)=>new BusinessManagementResponse($collection),$collection);
    }
}

